      out.write("	var fixBox = document.querySelector('.bottomNav');\r\n");
      out.write("	var fixBoxHeight = fixBox.offsetHeight;\r\n");
      out.write("	var didScroll;\r\n");
      out.write("	//스크롤 이벤트 \r\n");
      out.write("	window.onscroll = function(e) {\r\n");
      out.write("	    didScroll = true;\r\n");
      out.write("	};\r\n");
      out.write("\r\n");
      out.write("	//0.25초마다 스크롤 여부 체크하여 스크롤 중이면 hasScrolled() 호출\r\n");
      out.write("	setInterval(function(){\r\n");
      out.write("	    if(didScroll){\r\n");
      out.write("	        hasScrolled();\r\n");
      out.write("	        didScroll = false;\r\n");
      out.write("	    }\r\n");
