      out.write("	function hasScrolled(){\r\n");
      out.write("	    var nowScrollTop = window.scrollY;\r\n");
      out.write("	    if(Math.abs(lastScrollTop - nowScrollTop) <= delta){\r\n");
      out.write("	        return;\r\n");
      out.write("	    }\r\n");
      out.write("	    if(nowScrollTop > lastScrollTop && nowScrollTop > fixBoxHeight){\r\n");
      out.write("	        //Scroll down\r\n");
      out.write("	        fixBox.classList.add('show');\r\n");
      out.write("	    }else{\r\n");
      out.write("	        if(nowScrollTop + window.innerHeight < document.body.offsetHeight){\r\n");
      out.write("	            //Scroll up\r\n");
      out.write("	            fixBox.classList.remove('show');\r\n");
      out.write("	        }\r\n");
      out.write("	    }\r\n");
      out.write("	    lastScrollTop = nowScrollTop;\r\n");
      out.write("	};\r\n");
